mapper.select:
SELECT num,writer,content,target_id, TO_CHAR(regdate, 'YYYY.MM.DD DAY HH24:MI') AS regdate FROM (SELECT result1.*, ROWNUM rnum FROM (SELECT num,writer,content,target_id,regdate FROM visitor_comment
OR writer LIKE '%'||#{writer}||'%'
ORDER BY num DESC ) result1 ) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

SELECT result1.* FROM (SELECT num,writer,content,target_id, TO_CHAR(regdate, 'YYYY.MM.DD DAY HH24:MI') AS regdate FROM visitor_comment
OR writer LIKE '%'||#{writer}||'%'
ORDER BY num DESC) result1 WHERE num=#{num}
